mapper.select:
select user_id userId, user_name userName, user_password userPass
from trip_member
where user_id = #{userId} and user_password = #{userPass}

mapper.update:
update trip_member
set user_id = #{userId}, user_password = #{userPass}, user_name = #{userName}
where user_id = #{userId}

mapper.select:
select user_id userId, user_name userName, user_password userPass
from trip_member
where user_id = #{userId}
