mapper.update:
update plan_board
set article_title = #{articleTitle}
where article_no = #{articleNo}

update.foreach:
update review
set content = #{review.content}
where article_no = #{articleNo} and content_id = #{review.contentId}

mapper.select:
select save_folder, original_file, save_file
from file_info
where article_no = #{articleNo}
